DROP TABLE  `SOLAR_HEATING`;
create table  `SOLAR_HEATING`
(
    ID                      int auto_increment
        primary key,
    DAY                     int        not null,
    FROM_TIME               time       null,
    TO_TIME                 time       not null,
    `3WAY_VALVE_DIFF_START` double     null,
    `3WAY_VALVE_DIFF_STOP`  double     null,
    BOILER_BLOCKED          tinyint(1) null
);

